mapper.insert:

insert into save (no, user_no, problem_no) 
	 values((select s.no 
	           from save s 
	          where s.user_no = #{authUserNo } 
	            and s.problem_no = #{problemNo }), #{authUserNo },#{problemNo }) 
 	 on duplicate key update user_no = #{authUserNo }, problem_no = #{problemNo }
