mapper.update:

			update user 
   			   set nickname = #{nickname }
 			 where no = #{no }


			update user 
   			   set password = #{password }
 			 where no = #{no }

mapper.delete:

			delete
  			  from user
 			 where no = #{no } and email = #{email } and password = #{password }

mapper.select:

			select password
  			  from user
			 where email = #{email }

mapper.update:

update problem
   set state = 'n'
 where no = #{no }

mapper.select:

select no, title, contents, exam_input as examInput, correct_code as correctCode, exam_input as examInput, problem_no as problemNo, state
from sub_problem sp
where problem_no = #{no }
